mapper.select:
SELECT FROM sys_user WHERE is_deleted = 0
AND (phone LIKE CONCAT('%', #{keyword}, '%') OR nickname LIKE CONCAT('%', #{keyword}, '%'))
ORDER BY create_time DESC LIMIT #{offset}, #{pageSize}

mapper.update:
UPDATE sys_user
SET update_time = #{updateTime}
WHERE id = #{id}

UPDATE sys_user
SET is_deleted = 1
WHERE id = #{id}
