mapper.select:
SELECT d.id, d.doctor_name, d.hospital_id, d.dept_id, d.title, d.specialty, d.academic_background, d.schedule_time, d.consultation_fee, d.rating, d.review_count, d.is_deleted, d.create_time, d.update_time, h.hospital_name, dept.dept_name FROM doctor_info d LEFT JOIN hospital_info h ON d.hospital_id = h.id AND h.is_deleted = 0 LEFT JOIN hospital_department dept ON d.dept_id = dept.id AND dept.is_deleted = 0
d.is_deleted = 0
AND d.doctor_name LIKE CONCAT('%', #{doctorName}, '%')
AND d.hospital_id = #{hospitalId}
AND d.dept_id = #{deptId}
AND d.title = #{title}
AND d.specialty LIKE CONCAT('%', #{specialty}, '%')
AND d.rating >= #{minRating}
AND d.consultation_fee <= #{maxFee}
ORDER BY d.rating DESC, d.review_count DESC

SELECT d.id, d.doctor_name, d.hospital_id, d.dept_id, d.title, d.specialty, d.academic_background, d.schedule_time, d.consultation_fee, d.rating, d.review_count, d.is_deleted, d.create_time, d.update_time, h.hospital_name, dept.dept_name FROM doctor_info d LEFT JOIN hospital_info h ON d.hospital_id = h.id AND h.is_deleted = 0 LEFT JOIN hospital_department dept ON d.dept_id = dept.id AND dept.is_deleted = 0
d.is_deleted = 0
AND d.hospital_id = #{hospitalId}
AND d.dept_id = #{deptId}
AND d.title = #{title}
ORDER BY d.rating DESC, d.review_count DESC

SELECT d.id, d.doctor_name, d.hospital_id, d.dept_id, d.title, d.specialty, d.academic_background, d.schedule_time, d.consultation_fee, d.rating, d.review_count, d.is_deleted, d.create_time, d.update_time, h.hospital_name, dept.dept_name FROM doctor_info d LEFT JOIN hospital_info h ON d.hospital_id = h.id AND h.is_deleted = 0 LEFT JOIN hospital_department dept ON d.dept_id = dept.id AND dept.is_deleted = 0 WHERE d.is_deleted = 0
AND (d.doctor_name LIKE CONCAT('%', #{keyword}, '%') OR d.specialty LIKE CONCAT('%', #{keyword}, '%'))
AND d.hospital_id = #{hospitalId}
AND d.dept_id = #{deptId}
AND d.title = #{title}
ORDER BY
d.rating DESC d.consultation_fee ASC d.review_count DESC d.create_time DESC

SELECT COUNT(*) FROM doctor_info WHERE is_deleted = 0
AND (doctor_name LIKE CONCAT('%', #{keyword}, '%') OR specialty LIKE CONCAT('%', #{keyword}, '%'))
AND hospital_id = #{hospitalId}
AND dept_id = #{deptId}

SELECT 
FROM doctor_info
WHERE is_deleted = 0

    AND hospital_id = #{hospitalId}

ORDER BY rating DESC, review_count DESC
LIMIT #{limit}

mapper.insert:
INSERT INTO doctor_info (
    doctor_name, hospital_id, dept_id, title, specialty, academic_background,
    schedule_time, consultation_fee, rating, review_count, is_deleted, create_time, update_time
) VALUES (
    #{doctorName}, #{hospitalId}, #{deptId}, #{title}, #{specialty}, #{academicBackground},
    #{scheduleTime}, #{consultationFee}, #{rating}, #{reviewCount}, #{isDeleted}, #{createTime}, #{updateTime}
)

mapper.update:
UPDATE doctor_info
SET rating = #{rating}, review_count = #{reviewCount}, update_time = #{updateTime}
WHERE id = #{id}

UPDATE doctor_info
SET is_deleted = 1
WHERE id = #{id}

UPDATE doctor_info
SET is_deleted = 1
WHERE dept_id = #{deptId}

UPDATE doctor_info
SET is_deleted = 0
WHERE hospital_id = #{hospitalId}
