mapper.select:
SELECT 
FROM community_like
WHERE user_id = #{userId} AND target_type = #{targetType}
AND target_id IN

    #{targetId}

AND is_deleted = 0

mapper.update:
UPDATE community_like
SET is_deleted = 1
WHERE user_id = #{userId} AND target_type = #{targetType} AND target_id = #{targetId}

UPDATE community_like
SET is_deleted = 0
WHERE user_id = #{userId} AND target_type = #{targetType} AND target_id = #{targetId}

UPDATE community_like
SET is_deleted = ABS(is_deleted - 1)
WHERE user_id = #{userId} AND target_type = #{targetType} AND target_id = #{targetId}

mapper.delete:
DELETE FROM community_like
WHERE id IN

    #{id}
