mapper.select:
SELECT 
FROM user_medical_history

    is_deleted = 0

        AND user_id = #{userId}


        AND disease_name LIKE CONCAT('%', #{diseaseName}, '%')


        AND status = #{status}


        AND diagnosis_date >= #{startDate}


        AND diagnosis_date <= #{endDate}


ORDER BY diagnosis_date DESC, create_time DESC

mapper.update:
UPDATE user_medical_history
SET status = #{status}, update_time = #{updateTime}
WHERE id = #{id}

UPDATE user_medical_history
SET is_deleted = 1
WHERE id = #{id}

UPDATE user_medical_history
SET is_deleted = 1
WHERE user_id = #{userId}
